import React from 'react';
import { useParams } from 'react-router-dom';
import { products } from '../data/products';  // 데이터 임포트

const ProductDetail = () => {
    const { id } = useParams();  // URL에서 id를 가져옵니다.
    const product = products.find((product) => product.id === parseInt(id));  // 해당 상품을 찾습니다.

    if (!product) {
        return <div>상품을 찾을 수 없습니다.</div>;
    }

    return (
        <div className="min-h-screen bg-gray-50">
            <div className="max-w-7xl mx-auto px-4 py-12">
                <div className="flex gap-8">
                    <img src={product.images[0]} alt={product.name} className="w-1/2 object-cover" />
                    <div className="w-1/2">
                        <h1 className="text-3xl font-bold mb-4">{product.name}</h1>
                        <p className="text-lg mb-4">{product.description}</p>
                        <div className="text-lg font-bold text-blue-500">
                            {product.price.toLocaleString()}원
                        </div>
                        <div className="mt-4">
                            <button className="bg-blue-500 text-white py-2 px-6 rounded-lg hover:bg-blue-600">
                                장바구니에 추가
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ProductDetail;
