import React from 'react';
import { useParams } from 'react-router-dom';
import { products } from '../data/products';  // 데이터 임포트
import { productDetails } from '../data/productDetailData';

const ProductDetail = () => {
    const product = productDetails;

    if (!product) return <div>상품을 찾을 수 없습니다.</div>;

    return (
        <div className="max-w-7xl mx-auto px-4 py-12">
            <div className="flex flex-col lg:flex-row gap-8">
                {/* Left Side - Image Gallery */}
                <div className="flex flex-col items-center lg:w-1/2">
                    {/* Thumbnail List */}
                    <div className="flex lg:flex-col gap-2 mb-4">
                        {product.images.map((image, index) => (
                            <img
                                key={index}
                                src={image}
                                alt={`Thumbnail ${index + 1}`}
                                className="w-16 h-16 object-cover rounded-lg cursor-pointer border-2 border-transparent hover:border-gray-300"
                            />
                        ))}
                    </div>
                    {/* Main Image */}
                    <div className="w-full h-96">
                        <img
                            src={product.images[0]}
                            alt={product.name}
                            className="w-full h-full object-cover rounded-lg"
                        />
                    </div>
                </div>

                {/* Right Side - Product Information */}
                <div className="lg:w-1/2">
                    <h2 className="text-sm font-medium text-gray-500 mb-2">{product.brand}</h2>
                    <h1 className="text-3xl font-bold mb-4">{product.name}</h1>

                    {/* Rating and Reviews */}
                    <div className="flex items-center mb-4">
                        <span className="text-lg text-yellow-400 mr-2">★</span>
                        <span className="font-medium text-gray-700">{product.rating}</span>
                        <span className="text-sm text-gray-500 ml-2">({product.reviews.toLocaleString()} 리뷰)</span>
                    </div>

                    {/* Price and Discount */}
                    <div className="flex items-center mb-4">
                        <span className="text-2xl font-bold text-blue-600 mr-2">
                            {product.price.toLocaleString()}원
                        </span>
                        {product.discount > 0 && (
                            <>
                                <span className="text-gray-500 line-through mr-2">
                                    {product.originalPrice.toLocaleString()}원
                                </span>
                                <span className="text-red-500 font-medium">{product.discount}% 할인</span>
                            </>
                        )}
                    </div>

                    {/* Shipping Info */}
                    <div className="text-sm text-gray-700 mb-4">
                        {product.specialDelivery && (
                            <div className="text-purple-600 font-bold mb-1">오늘출발</div>
                        )}
                        <div>배송비: {product.shippingCost.toLocaleString()}원 (₩{product.freeShippingThreshold.toLocaleString()} 이상 구매 시 무료배송)</div>
                        <div className="text-gray-400">도서산간 지역 추가 비용 발생 가능</div>
                    </div>

                    {/* Expected Delivery Date */}
                    <div className="text-sm text-gray-700 mb-4">
                        <span className="text-gray-500">도착 예정일:</span>
                        <span className="text-blue-500 font-semibold ml-2">{product.estimatedDelivery}</span>
                    </div>

                    {/* Add to Cart Button */}
                    <button className="w-full bg-blue-500 text-white py-3 rounded-lg hover:bg-blue-600 transition-colors font-bold">
                        장바구니에 추가
                    </button>

                    {/* Additional Information */}
                    <div className="mt-8">
                        <h3 className="text-lg font-semibold mb-2">추가상품 (선택)</h3>
                        <select className="w-full border-gray-300 rounded-lg p-3">
                            {product.options.map((option, index) => (
                                <option key={index}>{option}</option>
                            ))}
                        </select>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ProductDetail;
